package main

import (
	"testing"
)

func TestDay2part1(t *testing.T) {
	res := day2part1(`ULL
RRDDD
LURDL
UUUUD`)

	if res != "1985" {
		t.Error("Unexpected result: Expected: ", res)
	}

	res = day2part1(`



`)

	if res != "92435" {
		t.Error("Unexpected result: Expected: 92435", "Got: ", res)
	}
}

func TestDay2part2(t *testing.T) {
	res := day2part2(`ULL
RRDDD
LURDL
UUUUD`)

	if res != "5DB3" {
		t.Error("Unexpected result: Expected: 5DB3 Got: ", res)
	}

	res = day2part2(`



`)

	if res != "C1A88" {
		t.Error("Unexpected result: Expected: C1A88", "Got: ", res)
	}
}
